# include<stdio.h>

int main(){
    int i = 5;
    printf("The value after i++ is %d\n", ++i);
    // ++i phle incteament fir priint
    // i++ phle print fir increament
    i++;
 
    printf("The value i is %d\n", i);

    printf("The value after i-- is %d\n", --i);
    printf("The value i is %d\n", --i);
     // --i phle decrease fir priint
    // i-- phle print fir decrease
    return 0;
}